const fs = require("fs");
const path = require("path");
const config = require("./config.json");

const content = `module.exports = window.${ config["global-name"] } || (function(){
    function loader(){
        console.warn("页面中没有引入加载器");
    }

    loader.register = loader.async = loader.plugin = loader;

    return loader;
})();`;

fs.writeFileSync(
    path.join(__dirname, "loader.js"),
    content
);