const tasks = {};

// 注册一个插件
export function register(point, plugin){
    if(!tasks[point]){
        tasks[point] = [];
    }

    tasks[point].push(plugin);
}

// 执行某个挂载点的插件
export function task(point, info){
    if(!tasks[point]){
        return info;
    }

    return tasks[point].reduce(function(info, task){
        return task(info);
    }, info);
}