/// <reference types="react" />

import React from "react";

export interface MonthPickerProps
  extends React.HtmlHTMLAttributes<HTMLHtmlElement> {
  /**
   * 品牌样式前缀
   */
  prefix?: string;

  /**
   * 默认的基准日期
   */
  defaultBase?: string;

  /**
   * 默认日期
   */
  defaultValue?: any;

  /**
   * 日期值
   */
  value?: any;

  /**
   * 日期发生改变时的回调
   */
  onChange?: (() => void);

  /**
   * 是否默认展开
   */
  defaultOpen?: boolean;

  /**
   * 是否展开
   */
  open?: boolean;

  /**
   * 当面板展开变化时的回调
   */
  onOpenChange?: (() => void);

  /**
   * 输入框大小
   */
  size?: "small" | "medium" | "large";

  /**
   * 语言
   */
  language?:
    | "en-us"
    | "ar"
    | "de"
    | "es"
    | "fr"
    | "hi"
    | "it"
    | "ja"
    | "ko"
    | "pt"
    | "ru"
    | "zh-cn"
    | "zh-hk"
    | "zh-tw";

  /**
   * 国际化配置, [参考](http://gitlab.alibaba-inc.com/next/date-picker/blob/master/src/locale/index.js)
   */
  locale?: {};

  /**
   * 弹层样式
   */
  popupStyle?: {};

  /**
   * 弹层对齐方式
   */
  popupAlign?: string;

  /**
   * 弹层偏移
   */
  popupOffset?: Array<any>;

  /**
   * 展开日期的格式
   */
  format?: string;

  /**
   * 是否禁用
   */
  disabled?: boolean;

  /**
   * 是否有清空按钮
   */
  hasClear?: boolean;

  /**
   * 输入框是否只读
   */
  readOnly?: boolean;

  /**
   * 自定义组件样式
   */
  className?: string;
}

export class MonthPicker extends React.Component<MonthPickerProps, any> {}

export interface RangePickerProps
  extends React.HtmlHTMLAttributes<HTMLHtmlElement> {
  /**
   * 品牌样式前缀
   */
  prefix?: string;

  /**
   * 默认的基准日期
   */
  defaultBase?: string;

  /**
   * 默认日期值
   */
  defaultValue?: any;

  /**
   * 日期值
   */
  value?: Array<any>;

  /**
   * 时间发生改变的回调
   */
  onChange?: (() => void);

  /**
   * 开始日期变化时的回调
   */
  onStartChange?: (() => void);

  /**
   * 结束日期变化时的回调
   */
  onEndChange?: (() => void);

  /**
   * 默认是否展开
   */
  defaultOpen?: boolean;

  /**
   * 是否展开
   */
  open?: boolean;

  /**
   * 当面板展开状态改变时的回调
   */
  onOpenChange?: (() => void);

  /**
   * 输入框大小
   */
  size?: "small" | "medium" | "large";

  /**
   * 语言
   */
  language?:
    | "en-us"
    | "ar"
    | "de"
    | "es"
    | "fr"
    | "hi"
    | "it"
    | "ja"
    | "ko"
    | "pt"
    | "ru"
    | "zh-cn"
    | "zh-hk"
    | "zh-tw";

  /**
   * 国际化配置，[参考](http://gitlab.alibaba-inc.com/next/date-picker/blob/master/src/locale/index.js)
   */
  locale?: {};

  /**
   * 弹层样式
   */
  popupStyle?: {};

  /**
   * 弹层对其方式
   */
  popupAlign?: string;

  /**
   * 弹层偏移
   */
  popupOffset?: Array<any>;

  /**
   * 输出日期的格式
   */
  format?: string | (() => void);

  /**
   * 日期值格式化（可替代 format）
   */
  formater?: Array<any>;

  /**
   * 是否禁用
   */
  disabled?: boolean;

  /**
   * 是否有清空按钮
   */
  hasClear?: boolean;

  /**
   * 是否显示时间或传入 { defaultValue } 设置时间初值
   */
  showTime?: boolean | {};

  /**
   * 重新选择时是否重置时间
   */
  resetTime?: boolean;

  /**
   * 用户预设的快捷选择的日期范围
   */
  ranges?: {};

  /**
   * 输入框是否只读
   */
  readOnly?: boolean;

  /**
   * 自定义组件样式
   */
  className?: string;
}

export class RangePicker extends React.Component<RangePickerProps, any> {}

